import fetch from "isomorphic-unfetch";

const url = "https://another-test-store-97213.myshopify.com/api/graphql";
const headers = {
  "Content-Type": "application/graphql",
  "X-Shopify-Storefront-Access-Token": "5db76a7bcd70717eade01e6a64392829"
};

let builder = request => {
  fetch(url, {
    method: "post",
    headers: headers,
    body: request
  })
    .then(r => r.json())
    .then(data => {
      return data;
    });
};

let queries = {
  getProductsWithImages: numberOfProducts => {
    let query = `query	{
      products(first:${numberOfProducts})	{
        edges {
          node {
            id
            title
            images(first: 1) {
              edges {
                node {
                  id
                }
              }
            }
            priceRange {
              maxVariantPrice {
                amount
              }
            }
          }
        }
      }
    }`;
    fetch("https://another-test-store-97213.myshopify.com/api/graphql", {
      method: "post",
      headers: {
        "Content-Type": "application/graphql",
        "X-Shopify-Storefront-Access-Token": "5db76a7bcd70717eade01e6a64392829"
      },
      body: query
    })
      .then(r => r.json())
      .then(data => console.log(data));
  },
  getShopName: () => {
    return `query	{
      shop {
        name
      }
    }
    `;
  }
};

export default queries;
