import { Component } from "react";
import Layout from "../components/MyLayout";
import Product from "../components/shop/Product";
import queries from "../queries/shop.js";

class Shop extends Component {
  constructor(props) {
    super(props);
    this.state = {
      shopName: "",
      products: ["cheeseburger"]
    };

    this.componentDidMount = this.compondentDidMount.bind(this);
  }

  componentDidMount() {
    queries.getShopName.then(data => this.setState({ shopName: data }));
    queries.getProductsWithImages(10).then(value => {
      this.setState({ products: value });
    });
    this.forceUpdate();
  }

  products() {
    if (this.state.products > 0) {
      return this.state.products.map((product, index) => {
        <p>{product.node.title}</p>;
      });
    } else {
      return "Cheese";
    }
  }

  render() {
    console.log(this.state);
    return (
      <Layout page="Shop">
        <p>This will eventually connect to a shopify store.</p>
        <h2>{this.state.shopName}</h2>
        <div className="products-container">{this.products}</div>
      </Layout>
    );
  }
}

export default Shop;
