import { Component } from "react";
import Layout from "../components/MyLayout";
import fetch from "isomorphic-unfetch";
import queries from "../queries/shop.js";

class Shop extends Component {
  constructor(props) {
    super(props);
    this.state = {
      shopName: "",
      products: []
    };
  }

  componentDidMount() {
    fetch("https://another-test-store-97213.myshopify.com/api/graphql", {
      method: "post",
      headers: {
        "Content-Type": "application/graphql",
        "X-Shopify-Storefront-Access-Token": "5db76a7bcd70717eade01e6a64392829"
      },
      body: queries.getShopName()
    })
      .then(r => r.json())
      .then(data => console.log(data));
  }

  render() {
    console.log(this.state);
    return (
      <Layout page="Shop">
        <h2>{this.state.shopName}</h2>
        <p>This will eventually connect to a shopify store.</p>
      </Layout>
    );
  }
}

export default Shop;
