export default function BlogPost(props) {
  return (
    <div className="BlogPost">
      <h1>{props.title}</h1>
      <div className="image-container">
        <img src={props.imgPath}></img>
      </div>
      <div className="text">
        {props.text.map(line => (
          <p>{line}</p>
        ))}
      </div>
      <style>{`
      .BlogPost {
        background: gray;
      }
      h1 {
        text-align: center;
      }
      .image-container {
        width: 100%;
        text-align: center;
      }
      img {
        width: 100%;
      }
  `}</style>
    </div>
  );
}
