import { Component } from "react";
import Layout from "../components/MyLayout";
import Product from "../components/shop/Product";
import queries from "../queries/shop.js";

class Shop extends Component {
  constructor(props) {
    super(props);
    this.state = {
      shopName: "",
      products: [],
      isLoaded: false
    };
  }

  componentDidMount() {
    // queries.getShopName.then(data =>
    //   this.setState({ shopName: data, isLoaded: true })
    // );
    // queries.getProductsWithImages(10).then(value => {
    //   this.setState({ products: value, isLoaded: true });
    // });
    const url = "https://another-test-store-97213.myshopify.com/api/graphql";
    const headers = {
      "Content-Type": "application/graphql",
      "X-Shopify-Storefront-Access-Token": "5db76a7bcd70717eade01e6a64392829"
    };
    let numberOfProducts = 2;
    let query = `query	{
  products(first:${numberOfProducts})	{
    edges {
      node {
        id
        title
        images(first: 1) {
          edges {
            node {
              id
            }
          }
        }
        priceRange {
          maxVariantPrice {
            amount
          }
        }
      }
    }
  }
}`;
    fetch(url, {
      method: "post",
      headers: headers,
      body: query
    })
      .then(r => r.json())
      .then(data => {
        this.setState({ products: data.data.products.edges });
      });
  }

  render() {
    // console.log(this.state);
    const { isLoaded, shopName, products } = this.state;
    console.log(products);
    if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <Layout page="Shop">
          <p>This will eventually connect to a shopify store.</p>
          <h2>{shopName}</h2>
          <div className="products-container">
            {products.map((product, index) => {
              <p>{product.node.title}</p>;
            })}
          </div>
        </Layout>
      );
    }
  }
}

export default Shop;
