import { Component } from "react";
import Client from "graphql-js-client";
import { gql } from "babel-plugin-graphql-js-client-transform";
import Layout from "../components/MyLayout";
import typeBundle from "../types";

const client = new Client(typeBundle, {
  url: "https://another-test-store-97213.myshopify.com/api/graphql",
  fetcherOptions: {
    headers: {
      "X-Shopify-Storefront-Access-Token": "5db76a7bcd70717eade01e6a64392829"
    }
  }
});

class Shop extends Component {
  constructor(props) {
    super(props);
    this.state = {
      shopName: "",
      products: []
    };
  }

  componentDidMount() {
    const client = this.props.client;
    // let query = client.query(root => {
    //   root.add("shop", shop => {
    //     shop.add("name");
    //     shop.addConnection("products", { args: { first: 10 } }, product => {
    //       product.add("title");
    //       product.add("description");
    //       product.addConnection("images", { args: { first: 1 } }, image => {
    //         image.add("id");
    //       });
    //     });
    //   });
    // });
    client
      .send(
        gql(client)`
    query {
      shop {
        name
        description
        products(first:20) {
          pageInfo {
            hasNextPage
            hasPreviousPage
          }
          edges {
            node {
              id
              title
              options {
                name
                values
              }
              variants(first: 250) {
                pageInfo {
                  hasNextPage
                  hasPreviousPage
                }
                edges {
                  node {
                    title
                    selectedOptions {
                      name
                      value
                    }
                    image {
                      src
                    }
                    price
                  }
                }
              }
              images(first: 250) {
                pageInfo {
                  hasNextPage
                  hasPreviousPage
                }
                edges {
                  node {
                    src
                  }
                }
              }
            }
          }
        }
      }
    }
  `
      )
      .then(({ model, data }) => {
        this.setState({
          shopName: data.shop.name,
          products: data.shop.products.edges
        });
        // console.log(model); // The serialized model with rich features
        // console.log(data); // The raw data returned from the API endpoint
        // console.log(name);
      });
  }

  render() {
    console.log(this.state);
    return (
      <Layout page="Shop">
        <h2>{this.state.shopName}</h2>
        <p>This will eventually connect to a shopify store.</p>
      </Layout>
    );
  }
}

Shop.defaultProps = {
  client: client
};

export default Shop;
