import Layout from "../components/MyLayout";
import Container from "../components/Container";
import { Controller, Scene } from "react-scrollmagic";

export default function Index() {
  return (
    <Layout page="Home">
      <div className="index">
        <div className="overlay">
          <Container>
            <h1>Software / Web Developer</h1>
            <p>Currently Based in San Diego, CA.</p>
            <p>
              Don't be shy. Send me an{" "}
              <a href="mailto:ryangrunest45@gmail.com">email</a>.
            </p>
          </Container>
        </div>
      </div>
      <div className="index-2">
        <div id="trigger"></div>
        <div />
        <Controller>
          <Scene
            duration={700}
            classToggle="slide-down-opacity"
            triggerElement="#trigger"
            indicators={true}
          >
            {(progress, event) => <div>event</div>}
          </Scene>
        </Controller>
      </div>
      <style jsx>{`
        @keyframes slidein {
          from {
            opacity: 0;
            transform: translateX(100%) rotate(-5deg);
          }
          to {
            opacity: 1;
            transform: translateX(0) rotate(-5deg);
          }
        }
        @keyframes opacity {
          from {
            opacity: 0;
          }
          to {
            opacity: 1;
          }
        }
        @keyframes slide-down-opacity {
          from {
            opacity: 0;
            transform: translateY(-5rem);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        @keyframes slide-up-opacity {
          from {
            opacity: 0;
            transform: translateY(5rem);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        .fadein {
          animation-name: opacity;
          animation-duration: 2s;
        }
        .slide-down-opacity {
          animation-name: slide-down-opacity;
          animation-duration: 2s;
        }
        h1 {
          text-align: center;
          transition: 0.3s all;
          transform: rotate(-5deg);
          text-transform: uppercase;
          color: #f0f0f0;
          animation-name: slidein;
          animation-duration: 2s;
        }
        h1:hover {
          transform: rotate(5deg);
        }
        p:first-of-type {
          margin-top: 4rem;
        }
        .overlay p {
          text-align: center;
          color: white;
          font-size: 15px;
          font-weight: 700;
          animation: opacity 3s ease-in;
        }
        .index {
          position: relative;
          background: url("/splash.jpg") no-repeat center center fixed;
          height: 48rem;
          z-index: 0;
        }
        .overlay {
          width: 100%;
          height: 100%;
          background-color: rgba(0, 0, 0, 0.3);
          position: absolute;
          display: flex;
          justify-content: center;
          align-items: center;
          flex-direction: column;
        }
        a {
          color: #f0f0f0;
        }
        a:hover {
          color: #ccbba2;
        }
        .padding-top {
          padding-top: 10rem;
        }
        .index-2 {
          height: 50rem;
        }
      `}</style>
    </Layout>
  );
}
