export default function BlogPost(props) {
  let splitText = props.text.split("\n");
  console.log(splitText);
  console.log(props);
  return (
    <div className="BlogPost">
      <h2 className="title">{props.title}</h2>
      <div className="image-container">
        <img src={props.imgPath}></img>
      </div>
      <h3 className="date">{props.date}</h3>
      <div className="text">
        {splitText.map(line => (
          <p>{line}</p>
        ))}
      </div>
      <style>{`
      .BlogPost {
        background: white;
        border-radius: 5px;
        box-shadow: 0px 0px 20px rgba(0,0,0,0.1);
        // margin: 1rem 0;
        padding-bottom: 2rem;
      }
      .title {
        text-align: center;
        margin: 0;
        padding: 2rem 1rem;
        text-transform: uppercase;
      }
      .image-container {
        width: 100%;
        text-align: center;
      }
      img {
        width: 100%;
      }
      .date {
        padding: 0 2rem;
        color: #687079;
      }
      .text {
        padding: 1rem 2rem;
      }
      .text p {
        text-indent: 2rem;
        font-size: 1.3rem;
        word-wrap: break-word;
        margin: 0;
      }
  `}</style>
    </div>
  );
}
