import { Component } from "react";
import Client from "graphql-js-client";
import { gql } from "babel-plugin-graphql-js-client-transform";
import Layout from "../components/MyLayout";
import typeBundle from "../types";

const client = new Client(typeBundle, {
  url: "https://another-test-store-97213.myshopify.com/api/graphql",
  fetcherOptions: {
    headers: {
      "X-Shopify-Storefront-Access-Token": "5db76a7bcd70717eade01e6a64392829"
    }
  }
});

class Shop extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  componentWillMount() {
    client
      .send(
        gql(client)`
      mutation {
        checkoutCreate(input: {}) {
          userErrors {
            message
            field
          }
          checkout {
            id
            webUrl
            subtotalPrice
            totalTax
            totalPrice
            lineItems (first:250) {
              pageInfo {
                hasNextPage
                hasPreviousPage
              }
              edges {
                node {
                  title
                  variant {
                    title
                    image {
                      src
                    }
                    price
                  }
                  quantity
                }
              }
            }
          }
        }
      }
    `
      )
      .then(res => {
        this.setState({
          checkout: res.model.checkoutCreate.checkout
        });
      });
  }

  render() {
    console.log(client);
    return (
      <Layout page="Shop">
        <p>This is the Shop page.</p>
        <p>This will eventually connect to a shopify store.</p>
      </Layout>
    );
  }
}

export default Shop;
