import React, { Component } from "react";
import Layout from "../components/MyLayout";
import BlogPost from "../components/BlogPost";

let blogArray = [
  {
    title: "First Blog Post",
    text:
      "Some Sample text Some Sample text Some Sample text Some Sample text Some Sample text Some Sample text Some Sample text Some Sample text Some Sample text",
    imgPath: "/blog-1.jpg"
  }
];

class Blog extends Component {
  constructor(props) {
    super(props);
    this.state = {
      numBlogs: blogArray.length
    };
    this.buttonClick = this.buttonClick.bind(this);
  }

  buttonClick = event => {
    event.preventDefault();
    this.setState({ numBlogs: this.state.numBlogs + 1 });
  };

  render() {
    return (
      <Layout page="Blog">
        <h1>
          This is the blog page. There are currently {this.state.numBlogs}{" "}
          posts.
        </h1>
        <button onClick={this.buttonClick}>Add A Post</button>
        {blogArray.map(blog => (
          <Blog title={blog.title} text={blog.text} imgPath={blog.imgPath} />
        ))}
        <style>{`
    `}</style>
      </Layout>
    );
  }
}

export default Blog;
