import React, { Component } from "react";
import firebase from "firebase/app";
import "firebase/database";
import {
  FirebaseDatabaseProvider,
  FirebaseDatabaseNode
} from "@react-firebase/database";
import dbconfig from "../dbconfig";
import Layout from "../components/MyLayout";
import BlogPost from "../components/BlogPost";

class Blog extends Component {
  constructor(props) {
    super(props);
    this.state = {
      numBlogs: 0,
      blogData: [],
      keys: "",
      values: "",
      entries: ""
    };
  }
  render() {
    console.log(this.state.entires);
    return (
      <>
        <FirebaseDatabaseProvider firebase={firebase} {...dbconfig}>
          <Layout page="Blog">
            {/* <h1>
              This is the blog page. There are currently {this.state.numBlogs}{" "}
              posts.
            </h1> */}
            <FirebaseDatabaseNode path="BlogPosts/" limitToFirst={3} orderByKey>
              {data => {
                if (data.value) {
                  this.setState({
                    blogData: data.value,
                    keys: Object.keys(data.value),
                    value: Object.values(data.value),
                    entires: Object.entries(data.value)
                  });
                  return "";
                } else {
                  // console.log(data);
                  return <div>Could not get data</div>;
                }
              }}
            </FirebaseDatabaseNode>
            {this.state.blogData.length < 1
              ? this.state.blogData.map((blog, index) => {
                  return (
                    <BlogPost
                      key={index}
                      title={blog.title}
                      text={blog.text}
                      imgPath={blog.imgPath}
                      date={blog.date}
                    />
                  );
                })
              : ""}
            <style>{`
    `}</style>
          </Layout>
        </FirebaseDatabaseProvider>
      </>
    );
  }
}

export default Blog;
